import { Request, Response, NextFunction } from 'express';
import { verify } from 'jsonwebtoken';
import { Op } from 'sequelize';

import Team from '../database/models/Team';

const secret = process.env.JWT_SECRET || 'jwt_secret';

interface Payload {
  id:number;
  role:string;
  iat:number;
}

const token = (req: Request, res: Response, next: NextFunction) => {
  const authHeader = req.header('Authorization');

  try {
    if (!authHeader || authHeader === null) {
      return res.status(401).json({ message: 'Token must be a valid token' });
    }
    verify(authHeader, secret) as Payload;

    next();
  } catch (_error) {
    return res.status(401).json({ message: 'Token must be a valid token' });
  }
};

const equalTeams = (req: Request, res: Response, next: NextFunction) => {
  const { homeTeamId, awayTeamId } = req.body;

  if (homeTeamId === awayTeamId) {
    return res.status(422)
      .json({ message: 'It is not possible to create a match with two equal teams' });
  }

  next();
};

const teamExist = async (req: Request, res: Response, next: NextFunction) => {
  const { homeTeamId, awayTeamId } = req.body;

  const searchResult = await Team.findAll({
    where: {
      [Op.or]: [{ id: homeTeamId }, { id: awayTeamId }],
    },
  });

  if (searchResult.length !== 2) {
    return res.status(404).json({ message: 'There is no team with such id!' });
  }

  next();
};

export default { token, equalTeams, teamExist };

import { Request, Response } from 'express';
import * as matchesService from '../service/matchesService';

const getByQuery = async (req: Request, res: Response) => {
  const { inProgress } = req.query;

  try {
    if (typeof inProgress !== 'string' || !inProgress) {
      const { httpStatusCode, response } = await matchesService.getAll();
      return res.status(httpStatusCode).json(response);
    }

    const { httpStatusCode, response } = await matchesService.getByQuery(inProgress);

    return res.status(httpStatusCode).json(response);
  } catch (error) {
    return res.status(500).json(error);
  }
};

const createMatch = async (req: Request, res: Response) => {
  const { homeTeamId, awayTeamId, homeTeamGoals, awayTeamGoals } = req.body;

  try {
    const { httpStatusCode, response } = await matchesService
      .createMatch(homeTeamId, awayTeamId, homeTeamGoals, awayTeamGoals);

    return res.status(httpStatusCode).json(response);
  } catch (error) {
    return res.status(500).json(error);
  }
};

const updateMatch = async (req: Request, res: Response) => {
  const { homeTeamGoals, awayTeamGoals } = req.body;
  const { id } = req.params;

  try {
    const { httpStatusCode, response } = await matchesService
      .updateMatch(Number(id), homeTeamGoals, awayTeamGoals);

    return res.status(httpStatusCode).json(response);
  } catch (error) {
    return res.status(500).json(error);
  }
};

const finishMatch = async (req: Request, res: Response) => {
  const { id } = req.params;

  try {
    const { httpStatusCode, response } = await matchesService.finishMatch(Number(id));

    return res.status(httpStatusCode).json(response);
  } catch (error) {
    return res.status(500).json(error);
  }
};

export default { getByQuery, createMatch, updateMatch, finishMatch };

import * as matchesRepository from '../repository/matchesRepository';

const getAll = async () => {
  const response = await matchesRepository.findAll();

  return { httpStatusCode: 200, response };
};

const getByQuery = async (q:string) => {
  const bool = JSON.parse(q);

  const response = await matchesRepository.findByQuery(bool);

  return { httpStatusCode: 200, response };
